mapper.update:
update RESTAURANT
set (location = {updateParam.location},
        restaurant_name = {updateParam.restaurant_name},
        category = {updateParam.category},
        standaed_time = {updatedParam.standaed_time}
        )
where id = #{id}

mapper.select:
select restaurant_id, res_user_id, goal_id, location, restaurant_name, category, standard_name
from RESTAURANT
where id = #{id}
